import React from 'react';
import { Link } from 'react-router-dom';
import './Compcss/Cardsec.css'
const CardSec = ({ tour }) => {
   if (!tour) {
      // Handle the case where 'tour' is undefined or null
      return null; // Or render a loading/error message
   }

   const { id, title, city, photo, price, featured } = tour;

   return (<>
    <a href={`/bookinglayout/${id}`} className='text-decoration-none text-dark'>
    
         <div className="card card_effect">
            <img src={photo} alt="tour-img" width="100%" className="card-img-top" />
            <div className="card-body">
               {featured && <span className="badge bg-warning">Featured</span>}
              
               <div className=" d-flex justify-content-between align-items-center break_box">
               <h5 className='card-title pt-2 text_title'>
                  {title}
               </h5>
                  <p className="card-text text_decpt">
                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-geo-alt" viewBox="0 0 16 16">
                  <path d="M12.166 8.94c-.524 1.062-1.234 2.12-1.96 3.07A31.493 31.493 0 0 1 8 14.58a31.481 31.481 0 0 1-2.206-2.57c-.726-.95-1.436-2.008-1.96-3.07C3.304 7.867 3 6.862 3 6a5 5 0 0 1 10 0c0 .862-.305 1.867-.834 2.94zM8 16s6-5.686 6-10A6 6 0 0 0 2 6c0 4.314 6 10 6 10z"/>
                  <path d="M8 8a2 2 0 1 1 0-4 2 2 0 0 1 0 4zm0 1a3 3 0 1 0 0-6 3 3 0 0 0 0 6z"/>
                  </svg> {city}
                  </p>
                 
               </div>

              
            <div className='d-flex justify-content-between align-items-end mt-2'>
            <div  className='text_decpt'>
                  <span className=''>&#8377;{price} </span>
                  <span className="text-muted">/per person</span>
               </div>
            <Link to={`/bookinglayout/${id}`} >
                  <button className='btn btn__color'>Explore</button>
               </Link>
            </div>
              
            </div>
         </div>
    
      </a>
   </>
     
   );
};

export default CardSec;
